/**
 * A standard or scoped role used when RBAC is turned on.
 */
class Role {

    /**
     * Has all permissions except cannot read or write resources related to the administrative audit logging system.
     */
    static get ADMINISTRATOR() {}

    /**
     * Can read anything. Can only modify the resources related to the administrative audit logging system.
     */
    static get AUDITOR() {}

    /**
     * Like a Maintainer, but with permission to modify persistent configuration constrained to resources that are
     * considered to be "application resources". A deployment is an application resource. The messaging server is not.
     * Items like datasources and JMS destinations are not considered to be application resources by default, but this
     * is configurable.
     */
    static get DEPLOYER() {}

    /**
     * Operator permissions, plus can modify the persistent configuration.
     */
    static get MAINTAINER() {}

    /**
     * A read-only role. Cannot modify any resource.
     */
    static get MONITOR() {}

    /**
     * Monitor permissions, plus can modify runtime state, but cannot modify anything that ends up in the persistent
     * configuration. Could, for example, restart a server.
     */
    static get OPERATOR() {}

    /**
     * Has all permissions. Equivalent to a JBoss AS 7 administrator.
     */
    static get SUPER_USER() {}

    /**
     * @return {string} the unique ID of this role.
     */
    get id() {}

    /**
     * @return {boolean} true if this is a standard role, false otherwise.
     */
    get standard() {}

    /**
     * @return {boolean} true if this is a scoped role, false otherwise.
     */
    get scoped() {}

    /**
     * @return {string} the name of this role.
     */
    get name() {}

    /**
     * @return {Role} the base role if this is a scoped role, null otherwise.
     */
    get baseRole() {}

    /**
     * @return {string[]} the scopes if this is a scoped role, an empty array otherwise.
     */
    get scope() {}

    /**
     * @return {string} a string representation of this role.
     */
    toString() {}
}

/**
 * Instance holding information about the console and its environment.
 */
class Environment {

    /**
     * @return {Version} the HAL version.
     */
    get halVersion() {}

    /**
     * @return {InstanceInfo} information about the server instance.
     */
    get instanceInfo() {}

    /**
     * @return {boolean} true for standalone mode, false otherwise.
     */
    get standalone() {}

    /**
     * @return {string} the name of the domain controller (DC).
     */
    get domainController() {}

    /**
     * @return {Version} the management model version.
     */
    get managementVersion() {}

    /**
     * @return {boolean} true if SSO is used, false otherwise.
     */
    get singleSignOn() {}

    /**
     * @return {Roles} the standard and scoped roles.
     */
    get roles() {}
}

/**
 * Holds information about an user.
 */
class User {

    /**
     * @return {string} the user name.
     */
    get name() {}

    /**
     * @return {boolean} true if this user belongs to the role SuperUser, false otherwise.
     */
    get superuser() {}

    /**
     * @return {boolean} true if this user belongs to the role Administrator, false otherwise.
     */
    get administrator() {}

    /**
     * @return {Role[]} the roles of this user.
     */
    get roles() {}

    /**
     * @return {User} the current user.
     */
    static current() {}
}

/**
 * Contains information about the server instance the console is connected to or loaded from (taken from the root
 * resource).
 */
class InstanceInfo {

    /**
     * @return {string} the product name.
     */
    get productName() {}

    /**
     * @return {string} the product version.
     */
    get productVersion() {}

    /**
     * @return {string} the release name.
     */
    get releaseName() {}

    /**
     * @return {string} the release version.
     */
    get releaseVersion() {}

    /**
     * @return {string} the server name.
     */
    get serverName() {}
}

/**
 * Provides access to all standard and scoped roles.
 */
class Roles {

    /**
     * @return {Role[]} all roles (standard and scoped).
     */
    get all() {}

    /**
     * @return {Role[]} standard roles.
     */
    get standardRoles() {}

    /**
     * @return {Role[]} scoped roles or an empty array if no scoped roles are defined.
     */
    get scopedRoles() {}

    /**
     * @param {string} id The unique ID of the role.
     * 
     * @return {Role} the role for that ID or null if no such role was found.
     */
    get(id) {}
}

/**
 * Provides access to the endpoints used in HAL.
 */
class Endpoints {

    /**
     * @return {string} the endpoint used to execute management operations.
     */
    get dmr() {}

    /**
     * @return {string} the endpoint used for file uploads.
     */
    get upload() {}

    /**
     * @return {boolean} true if the console is served from a WildFly / EAP instance, false if it runs standalone and
     * connected to an arbitrary management endpoint.
     */
    get sameOrigin() {}
}

/**
 * Represents a version consisting of a major, minor and micro part.
 * 
 * @since 0.1.0
 */
class Version {

    /**
     * @return {number} the major version number.
     */
    get major() {}

    /**
     * @return {number} the minor version number.
     */
    get minor() {}

    /**
     * @return {number} the patch version number.
     */
    get micro() {}

    /**
     * Creates a new version as a result of parsing the specified version string.
     * 
     * @param {string} version The version string to parse
     * 
     * @return {Version} a new version
     */
    static valueOf(version) {}

    /**
     * @return {string} &lt;major&gt;.&lt;minor&gt;.&lt;micro&gt;
     */
    toString() {}
}

/**
 * Executes operations against the management endpoint.
 */
class Dispatcher {

    /**
     * Executes the specified composite operation.
     * 
     * @param {Composite} composite The composite operation to execute.
     * @param {function(result: CompositeResult)} callback  The callback receiving the result.
     */
    executeComposite(composite, callback) {}

    /**
     * Executes the specified operation. The callback contains just the result w/o surrounding nodes like "outcome".
     * 
     * @param {Operation} operation The operation to execute.
     * @param {function(result: ModelNode)} callback  The callback receiving the result.
     */
    execute(operation, callback) {}
}

/**
 * Represents a composite operation consisting of n {@link Operation}s.
 */
class Composite {

    /**
     * @return {boolean} whether this composite contains operations
     */
    get empty() {}

    /**
     * @return {number} the number of operations
     */
    get size() {}

    /**
     * @return {Operation[]} the operations of this composite
     */
    get operations() {}

    /**
     * Adds the specified operation to this composite.
     * 
     * @param {Operation} operation The operation to add.
     * 
     * @return {Composite} this composite
     */
    addOperation(operation) {}

    /**
     * @return {string} a string representation of this composite
     */
    toString() {}

    /**
     * @return {string} the string representation of the operation as used in the CLI
     */
    asCli() {}
}

/**
 * Represents a DMR property.
 */
class Property {

    /**
     * Creates a new property
     * 
     * @param {string} name  The name of the property.
     * @param {ModelNode} value The value of the property.
     */
    constructor(name, value) {}

    /**
     * @return {string} the name of the property
     */
    get name() {}

    /**
     * @return {ModelNode} the value of the property
     */
    get value() {}
}

/**
 * Static helper methods for dealing with {@link ModelNode}s and {@link NamedNode}s. Some methods accept a path
 * parameter separated by "/" to get a deeply nested data.
 */
class ModelNodeHelper {

    /**
     * Tries to get a deeply nested model node from the specified model node. Nested paths must be separated with "/".
     * 
     * @param {ModelNode} modelNode The model node to read from
     * @param {string} path      A path separated with "/"
     * 
     * @return {ModelNode} The nested node or an empty / undefined model node
     */
    static failSafeGet(modelNode, path) {}

    /**
     * Tries to get a deeply nested boolean value from the specified model node. Nested paths must be separated with
     * "/".
     * 
     * @param {ModelNode} modelNode The model node to read from
     * @param {string} path      A path separated with "/"
     * 
     * @return {boolean} the boolean value or false.
     */
    static failSafeBoolean(modelNode, path) {}

    /**
     * Tries to get a deeply nested node array from the specified model node. Nested paths must be separated with "/".
     * 
     * @param {ModelNode} modelNode The model node to read from
     * @param {string} path      A path separated with "/"
     * 
     * @return {ModelNode[]} the model nodes or an empty array
     */
    static failSafeList(modelNode, path) {}

    /**
     * Tries to get a deeply nested property array from the specified model node. Nested paths must be separated with
     * "/".
     * 
     * @param {ModelNode} modelNode The model node to read from
     * @param {string} path      A path separated with "/"
     * 
     * @return {Property[]} the properties or an empty array
     */
    static failSafePropertyList(modelNode, path) {}

    /**
     * Turns an properties array into an array of names nodes.
     * 
     * @param {Property[]} properties The properties
     * 
     * @return {NamedNode[]} the array of named nodes
     */
    static asNamedNodes(properties) {}
}

/**
 * Represents a fully qualified DMR address ready to be put into a DMR operation. The address consists of 0-n segments
 * with a name and a value for each segment.
 */
class ResourceAddress {

    /**
     * @return {string} the value of the first segment or null if this address is empty.
     */
    get firstValue() {}

    /**
     * @return {string} the name of the last segment or null if this address is empty.
     */
    get lastName() {}

    /**
     * @return {string} the value of the last segment or null if this address is empty.
     */
    get lastValue() {}

    /**
     * @return {ResourceAddress} the parent address or the root address if this address has no parent.
     */
    get parent() {}

    /**
     * @return {number} the number of segments.
     */
    get size() {}

    /**
     * @return {boolean} whether this address is empty.
     */
    get empty() {}

    /**
     * @return {ResourceAddress} the empty (root) address
     */
    static root() {}

    /**
     * Adds the specified segment to this address.
     * 
     * @param {string} propertyName  the property name
     * @param {string} propertyValue the property value
     * 
     * @return {ResourceAddress} this address with the specified segment added
     */
    addSegment(propertyName, propertyValue) {}

    /**
     * Adds the specified address to this address.
     * 
     * @param {ResourceAddress} address The address to add.
     * 
     * @return {ResourceAddress} this address with the specified address added
     */
    addAddress(address) {}

    /**
     * Replaces the value in the specified segment
     * 
     * @param {string} name     The name of the segment.
     * @param {string} newValue The new value.
     * 
     * @return {ResourceAddress} this address containing the replaced value
     */
    replaceValue(name, newValue) {}

    /**
     * @return {string} the address as string
     */
    toString() {}
}

/**
 * Represents a DMR operation.
 */
class Operation {

    /**
     * @return {string} the name of the operation
     */
    get name() {}

    /**
     * @return {ResourceAddress} the address of the operation
     */
    get address() {}

    /**
     * @return {ModelNode} the parameters of the operation
     */
    get parameter() {}

    /**
     * @return {ModelNode} the header of the operation
     */
    get header() {}

    /**
     * @return {string} the string representation of the operation as used in the CLI
     */
    toString() {}

    /**
     * @return {string} the string representation of the operation as used in the CLI
     */
    asCli() {}
}

/**
 * A dynamic model representation node object.
 */
class ModelNode {

    /**
     * Determine whether this node is defined.
     * 
     * @return {boolean} true if this node's value is defined, false otherwise
     */
    get defined() {}

    /**
     * @return {boolean} true if this node has an outcome and the outcome does not equal "success"
     */
    get failure() {}

    /**
     * @return {string} the failure description or "No failure-description provided"
     */
    get failureDescription() {}

    /**
     * Creates a new node from a base64 encoded string
     * 
     * @param {string} encoded The base64 encoded string.
     * 
     * @return {ModelNode} the new model node
     */
    static fromBase64(encoded) {}

    /**
     * Get the value of this node as number. Collection types will return the size
     * of the collection for this value. Other types may attempt a string conversion.
     * 
     * @return {number} the numeric value
     * 
     * @throws IllegalArgumentException if no conversion is possible
     */
    asInt() {}

    /**
     * Get the value of this node as a boolean. Collection types return true for non-empty collections. Numerical types
     * return true for non-zero values.
     * 
     * @return {boolean} the boolean value
     * 
     * @throws IllegalArgumentException if no conversion is possible
     */
    asBoolean() {}

    /**
     * Get the value as a string. This is the literal value of this model node. More than one node type may
     * yield the same value for this method.
     * 
     * @return {string} the string value
     */
    asString() {}

    /**
     * Get the value of this node as a property. Object values will return a property if there is exactly one
     * property in the object. List values will return a property if there are exactly two items in the list,
     * and if the first is convertible to a string.
     * 
     * @return {Property} the property value
     * 
     * @throws IllegalArgumentException if no conversion is possible
     */
    asProperty() {}

    /**
     * Get a copy of this value as an object. Object values will simply copy themselves.
     * <p>
     * Property values will return a single-entry object whose key and value are copied from the property key and value.
     * <p>
     * List values will attempt to interpolate the list into an object by iterating each item, mapping each property
     * into an object entry and otherwise taking pairs of list entries, converting the first to a string, and using the
     * pair of entries as a single object entry. If an object key appears more than once in the source object, the
     * last key takes precedence.
     * 
     * @return {ModelNode} the object value
     * 
     * @throws IllegalArgumentException if no conversion is possible
     */
    asObject() {}

    /**
     * Change this node's value to the given value.
     * 
     * @param {number} newValue the new value
     * 
     * @return {ModelNode} this node
     */
    setNumber(newValue) {}

    /**
     * Change this node's value to the given value.
     * 
     * @param {boolean} newValue the new value
     * 
     * @return {ModelNode} this node
     */
    setBoolean(newValue) {}

    /**
     * Change this node's value to the given expression value.
     * 
     * @param {string} newValue the new value
     * 
     * @return {ModelNode} this node
     */
    setExpression(newValue) {}

    /**
     * Change this node's value to the given value.
     * 
     * @param {string} newValue the new value
     * 
     * @return {ModelNode} this node
     */
    setString(newValue) {}

    /**
     * Change this node's value to the given value. The value is copied from the parameter.
     * 
     * @param {ModelNode} newValue the new value
     * 
     * @return {ModelNode} this node
     */
    setNode(newValue) {}

    /**
     * Change this node's value to the given value.
     * 
     * @param {Property} newValue the new value
     * 
     * @return {ModelNode} this node
     */
    setProperty(newValue) {}

    /**
     * Change this node's value to an empty list.
     * 
     * @return {ModelNode} this node
     */
    setEmptyList() {}

    /**
     * Change this node's value to an empty object.
     * 
     * @return {ModelNode} this node
     */
    setEmptyObject() {}

    /**
     * Clear this node's value and change its type to undefined.
     * 
     * @return {ModelNode} this node
     */
    clear() {}

    /**
     * Get the child of this node with the given name. If no such child exists, create it. If the node is undefined,
     * it will be initialized to be of type object.
     * <p>
     * When called on property values, the name must match the property name.
     * 
     * @param {string} name the child name
     * 
     * @return {ModelNode} the child
     * 
     * @throws IllegalArgumentException if this node does not support getting a child with the given name
     */
    get(name) {}

    /**
     * Add a node to the end of this node's value list and return it. If the node is undefined, it will be initialized
     * to be of type list.
     * 
     * @return {ModelNode} the new node
     */
    add() {}

    /**
     * Determine whether this node has a child with the given name. Property node types always contain exactly one
     * value with a key equal to the property name.
     * 
     * @param {string} key the name
     * 
     * @return {boolean} true if there is a (possibly undefined) node at the given key
     */
    has(key) {}

    /**
     * Determine whether this node has a defined child with the given name. Property node types always contain exactly
     * one value with a key equal to the property name.
     * 
     * @param {string} key the name
     * 
     * @return {boolean} true if there is a node at the given index and its type is not undefined
     */
    hasDefined(key) {}

    /**
     * Get a human-readable string representation of this model node, formatted nicely (possibly on multiple lines).
     * 
     * @return {string} the string representation
     */
    toString() {}


    toBase64String() {}

    /**
     * Creates a new undefined model node
     * @return {ModelNode} the new model node
     */
    static create() {}

    /**
     * @return {json} the model node as JSON
     */
    getJSON() {}

    /**
     * @return {Property[]} this model node as an {@link Property} array
     */
    asProperties() {}

    /**
     * @return {ModelNode[]} this model node as an array.
     */
    asList() {}
}

/**
 * A model node with a name.
 */
class NamedNode {

    /**
     * @return {string} the name of this named node
     */
    get name() {}

    /**
     * @return {ModelNode} the model node of this named node
     */
    get modelNode() {}

    /**
     * @return {string} a string representation of this model node
     */
    toString() {}


    setName(name) {}


    update(node) {}


    static create(name) {}
}

/**
 * Represents the result of a composite operation.
 */
class CompositeResult {

    /**
     * @return {number} the number of steps
     */
    get size() {}

    /**
     * @return {boolean} whether this composite result contains steps
     */
    get empty() {}

    /**
     * @return {ModelNode[]} the steps of this composite result
     */
    get steps() {}

    /**
     * @param {string} step Step as "step-n" (one-based!)
     * 
     * @return {ModelNode} the related step result
     */
    step(step) {}
}

/**
 * A builder for operations.
 */
class OperationBuilder {

    /**
     * Uses the specified payload for the operation.
     * 
     * @param {ModelNode} payload The operation as model node.
     * 
     * @return {OperationBuilder} this builder
     */
    payload(payload) {}

    /**
     * @return {Operation} builds and returns the operation
     */
    build() {}

    /**
     * Add a parameter to the operation
     * 
     * @param {string} name  The name of the parameter.
     * @param {boolean|int|string} value The value of the parameter.
     * 
     * @return {OperationBuilder} this builder
     */
    param(name, value) {}

    /**
     * Add a header to the operation
     * 
     * @param {string} name  The name of the header.
     * @param {boolean|int|string} value The value of the header.
     * 
     * @return {OperationBuilder} this builder
     */
    header(name, value) {}
}

/**
 * Template for a DMR address which might contain multiple variable parts.
 * 
 * An address template can be defined using the following BNF:
 * <pre>
 * &lt;address template&gt; ::= "/" | &lt;segment&gt;
 * &lt;segment&gt;          ::= &lt;tuple&gt; | &lt;segment&gt;"/"&lt;tuple&gt;
 * &lt;tuple&gt;            ::= &lt;variable&gt; | &lt;key&gt;"="&lt;value&gt;
 * &lt;variable&gt;         ::= "{"&lt;alpha&gt;"}"
 * &lt;key&gt;              ::= &lt;alpha&gt;
 * &lt;value&gt;            ::= &lt;variable&gt; | &lt;alpha&gt; | "*"
 * &lt;alpha&gt;            ::= &lt;upper&gt; | &lt;lower&gt;
 * &lt;upper&gt;            ::= "A" | "B" |  | "Z"
 * &lt;lower&gt;            ::= "a" | "b" |  | "z"
 * </pre>
 * 
 * Following variables are supported:
 * - <code>{domain.controller}</code>
 * - <code>{selected.profile}</code>
 * - <code>{selected.group}</code>
 * - <code>{selected.server-config}</code>
 * - <code>{selected.server}</code>
 * 
 * To get a fully qualified address from an address template use the method <code>resolve()</code>. For standalone mode
 * the variables will resolve to an empty string. The values of the variables are managed by the {@link
 * StatementContext}.
 * 
 * @example AddressTemplate a2 = AddressTemplate.of("{selected.profile}");
 * AddressTemplate a3 = AddressTemplate.of("{selected.profile}/subsystem=mail");
 * AddressTemplate a4 = AddressTemplate.of("{selected.profile}/subsystem=mail/mail-session=*");
 */
class AddressTemplate {

    /**
     * The root template
     */
    static get ROOT() {}

    /**
     * @return {boolean} true if this template contains no tokens, false otherwise
     */
    get empty() {}

    /**
     * @return {number} the number of tokens
     */
    get size() {}

    /**
     * @return {AddressTemplate} the parent address template or the root template
     */
    get parent() {}

    /**
     * @return {string} the name of the first segment or null if this address template is empty.
     */
    get firstName() {}

    /**
     * @return {string} the value of the first segment or null if this address template is empty.
     */
    get firstValue() {}

    /**
     * @return {string} the name of the last segment or null if this address template is empty.
     */
    get lastName() {}

    /**
     * @return {string} the value of the last segment or null if this address template is empty.
     */
    get lastValue() {}

    /**
     * Creates a new address template from an encoded string template.
     */
    static of(template) {}

    /**
     * @return {string} the string representation of this address template
     */
    toString() {}

    /**
     * Resolve this address template against the specified statement context.
     * 
     * @param {StatementContext} context   the statement context
     * @param {...string} wildcards An optional list of wildcards which are used to resolve any wildcards in this address template
     * from left to right
     * 
     * @return {ResourceAddress} a fully qualified resource address which might be empty, but which does not contain any tokens
     */
    resolve(context, wildcards) {}

    /**
     * Append an address to this addrress template and return a new one.
     * 
     * @param {string|AddressTemplate} address The address to append.
     * 
     * @return {AddressTemplate} a new address template with the specified address added at the end.
     */
    append(address) {}
}

/**
 * Registry for existing resource {@link Metadata}.
 */
class MetadataRegistry {

    /**
     * Checks whether there's a metadata for the specified template.
     * 
     * @param {AddressTemplate|String} template The address template to check.
     * 
     * @return {boolean} true if the registry contains metadata for the given template, false otherwise
     */
    contains(template) {}

    /**
     * Returns metadata associated with the specified address template.
     * 
     * @param {AddressTemplate|String} template The address template to lookup.
     * 
     * @return {Metadata} the metadata for the specified template
     * 
     * @throws MissingMetadataException if no metadata for the given template exists
     */
    lookup(template) {}
}

/**
 * Contains global state which is updated as you navigate in the console. In standalone mode most of the methods return
 * null.
 */
class StatementContext {

    /**
     * @return {string} the domain controller
     */
    get domainController() {}

    /**
     * @return {string} the selected profile
     */
    get selectedProfile() {}

    /**
     * @return {string} the selected server group
     */
    get selectedServerGroup() {}

    /**
     * @return {string} the selected host
     */
    get selectedHost() {}

    /**
     * @return {string} the selected server config
     */
    get selectedServerConfig() {}

    /**
     * @return {string} the selected server
     */
    get selectedServer() {}
}

/**
 * Represents the RBAC related payload from the read-resource-description operation.
 */
class SecurityContext {

    /**
     * A security context with hardcoded permissions to read resources, write and execute operations are not allowed.
     */
    static get READ_ONLY() {}

    /**
     * A security context with hardcoded permissions to read, write and execute any resource.
     */
    static get RWX() {}

    /**
     * @return {boolean} whether the security context is readable
     */
    get readable() {}

    /**
     * @return {boolean} whether the security context is writable
     */
    get writable() {}

    /**
     * @param {string} attribute The attribute to check.
     * 
     * @return {boolean} whether the attribute is readable
     */
    isReadable(attribute) {}

    /**
     * @param {string} attribute The attribute to check.
     * 
     * @return {boolean} whether the attribute is writable
     */
    isWritable(attribute) {}

    /**
     * @param {string} operation The operation to check.
     * 
     * @return {boolean} whether the operation is executable
     */
    isExecutable(operation) {}
}

/**
 * Reads resource {@link Metadata} using read-resource-description operations and stores it into the {@link
